mapper.select:
SELECT `user`.id, `user`.nick_name, `user`.email , `user`.photo
FROM `user`



            id = #{user.id}


            binary nick_name like concat('%',#{user.nickName},'%')


            email = #{user.email}


            1 = 2

mapper.delete:
